import React from "react";
import { motion, useViewportScroll, useTransform } from "framer-motion";

import "./Shapes.scss";

const Shapes = () => {
  const { scrollYProgress } = useViewportScroll();
  const scale = useTransform(scrollYProgress, [1, 1], [2, 0.2]);

  return (
    <motion.div className="app__shapes-container" style={{ scale }}>
      <motion.div
        // style={{
        //   scaleY: scrollYProgress,
        // }}
        initial={{ y: -scrollYProgress }}
        animate={{ y: 1000 }}
      >
        <i className="fa-brands fa-react fa-5x"></i>
      </motion.div>
    </motion.div>
  );
};

export default Shapes;
