import React from "react";
import { motion, useViewportScroll, useTransform } from "framer-motion";
import { images } from "../../constants";

import "./Shapes.scss";

const Shapes = () => {
  const { scrollY } = useViewportScroll();
  const y = useTransform(scrollY, [0, 100], [-300, -250]);
  const y1 = useTransform(scrollY, [0, 300], [-500, -450]);
  const y2 = useTransform(scrollY, [0, 300], [-900, -950]);

  return (
    <motion.div className="app__shapes-container">
      <motion.div style={{ y: y }}>
        <img
          src={images.cloudDark}
          alt="dark clouds"
          className="app__dark-clouds"
        />
      </motion.div>
      <motion.div style={{ y: y1 }} className="app__mountain">
        <img src={images.mountain} alt="mountain" />
      </motion.div>
      <motion.div
        style={{ y: y2 }}
        animate={{ x: [-200, 0] }}
        transition={{ yoyo: Infinity, duration: 2 }}
      >
        <img src={images.cloud} alt="cloud" />
      </motion.div>
    </motion.div>
  );
};

export default Shapes;
