import React, { useState } from "react";

import { AppWrap } from "../../wrapper";
import { client } from "../../client";
import "./Contact.scss";

const Contact = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });
  const [alert, setAlert] = useState({
    name: "",
    email: "",
    message: "",
  });
  const [isFormSubmitted, setisFormSubmitted] = useState(false);
  const [loading, setloading] = useState(false);

  const { name, email, message } = formData;

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    const contact = {
      _type: "contact",
      name: name,
      email: email,
      message: message,
    };

    // if (name == "" || !name.match(/^[a-zA-Z ]+$/)) {
    //   console.log(
    //     "Please enter a valid name (Only letters, -, and ' are acceptable)"
    //   );
    //   setAlert({
    //     ...alert,
    //     name: "Please enter a valid name (Only letters, -, and ' are acceptable)",
    //   });
    // } else if (
    //   email == "" ||
    //   !email.match(
    //     /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    //   )
    // ) {
    //   console.log(
    //     "Please enter a valid email address (Only letters, numbers, @, ., _, and - are acceptable)"
    //   );
    //   setAlert({
    //     ...alert,
    //     email:
    //       "Please enter a valid email address (Only letters, numbers, @, ., _, and - are acceptable)",
    //   });
    // } else if (message == "") {
    //   console.log("Please enter a message");
    //   setAlert({ ...alert, message: "Please enter a message" });
    // } else {
    setloading(true);

    client
      .create(contact)
      .then(() => {
        setloading(false);
        setisFormSubmitted(true);
      })
      .catch((error) => console.error(error));

    setTimeout(() => setisFormSubmitted(false), 3000);

    e.preventDefault();
  };

  return (
    <div className="app__contact app__flex">
      <h2 className="head-text">Contact</h2>

      {!isFormSubmitted ? (
        <form action="" className="app__contact-form app__flex">
          <div className="app__flex app__contact-input">
            <input
              className="p-text"
              placeholder="Your Name"
              type="text"
              name="name"
              value={name}
              onChange={handleChangeInput}
              required
            />
          </div>
          <div className="app__flex app__contact-input">
            <input
              className="p-text"
              placeholder="Your Email"
              type="email"
              name="email"
              value={email}
              onChange={handleChangeInput}
              required
            />
          </div>
          <div className="app__contact-textarea">
            <textarea
              className="p-text"
              placeholder="Your Message"
              value={message}
              name="message"
              onChange={handleChangeInput}
              cols="30"
              rows="10"
              required
            ></textarea>
          </div>
          <button type="submit" className="p-text" onClick={handleSubmit}>
            {loading ? "Sending" : "Send Message"}
          </button>
        </form>
      ) : (
        <div>
          <p className="p-text">Thank you for getting in touch!</p>
        </div>
      )}
    </div>
  );
};

export default AppWrap(Contact, "contact");
